mapper.select:

SELECT  *
FROM (
    SELECT rownum rn, bd_num, bd_title, bd_content, mem_id, bd_date_reg
	    FROM BOARD_TBL
	    WHERE bd_num > 0 and rownum > 0 and






        rownum <= #{pageNum} * #{amount}

    )

    where rn  > (#{pageNum}-1) * #{amount}

mapper.insert:
INSERT INTO BOARD_TBL(BD_NUM, BD_TITLE, BD_CONTENT, MEM_ID, MEM_PW, BD_DATE_REG)
VALUES (
	  SEQ_BOARD_TBL.NEXTVAL
	, #{bd_title}
	, #{bd_content}
	, #{mem_id}
	, #{mem_pw}
	, SYSDATE)

mapper.update:
	UPDATE BOARD_TBL
    SET BD_TITLE = #{bd_title}
        , BD_CONTENT = #{bd_content}
	WHERE BD_NUM = #{bd_num}
